        text-shadow: 0 0 12px rgba(255,255,255,0.6);
      }
    }

    .fa-chevron-left {
      position: absolute;
      top: 50%;
      left: 15px;
      transform: translateY(-50%);
    }

    .fa-chevron-right {
      position: absolute;
      top: 50%;
      right: 25px;
      transform: translateY(-50%);
    }

    .carousel-title-wrapper {
      position: absolute;
      top: 0;
      left: 0;
      padding: 1rem;
      transition: transform 0.6s ease-out;
      width: 90vw;
      cursor: pointer;

      .carousel-title {
        font-size: 1.5rem;
        color: white;
        position: relative;
        z-index: 5;

        &::before {
          content: '';
          position: absolute;
          width: 40%;
          max-width: 250px;
          height: 75%;
          bottom: -4px;
          left: -12px;
          background-color: $contrast2;
          z-index: -1;
        }
      }
    }

    img {
      width: 100%;
    }

    .img-dots {
      position: absolute;
      bottom: 15px;
      left: 50%;
      transform: translateX(-50%);
      display: flex;
      z-index: 10;

      .dot {
        width: 8px;
        height: 8px;
        border-radius: 50%;
        background-color: rgba(255,255,255,0.3);
        margin: 0 0.5rem;
        transition: background-color 0.4s ease;
        cursor: pointer;

        &:hover {
          box-shadow: 0 0 8px rgba(255,255,255,0.8);
        }

        &.active {
          background-color: rgba(255,255,255,0.7);
        }
      }
    }
  }
}


.drink-wrapper {
  #drinks-container {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    width: 85%;
    max-width: 1000px;
    margin: 0 auto;

    .card {
      width: 250px;
      height: 250px;
      border-radius: 50%;
      margin: 1rem;
      overflow: hidden;
      position: relative;
      cursor: pointer;
      z-index: 10;
      box-shadow: -8px 6px 0 $contrast2;

      select {
        border-radius: 10px;
        padding: 8px;
        appearance: none;
      }

      .img-cocktail {
        height: 100%;
        transition: transform 0.3s ease;

      }

      &:hover .img-cocktail {
        transform: scale(1.1);
      }

      .tab-cocktail {
        position: absolute;
        bottom: 0;
        left: 50%;
        padding: 3px 3px 10px 3px;
        text-align: center;
        background-color: rgba(0,0,0,0.4);
        color: #fff;
        transform: translateX(-50%);
        max-width: 100%;
        transition: padding-bottom 0.3s ease;

      }
      &:hover .tab-cocktail {
        padding-bottom: 32px;
      }
    }
  }

  .page-btn-container {
    display: flex;
    justify-content: center;

    .page-btn {
