      display: flex;
    }

    .selector-heading {
      font-size: 1.5rem;
      font-weight: 500;
      margin-bottom: 2rem;
    }

    h3 {
      font-size: 1.2rem;
      font-weight: 300;
      margin-right: 0.75rem;
    }
  }
}

