.modal-wrapper {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 10000;
  display: none;

  &.active {
    display: inline;
  }

  .overlay {
    position: absolute;
    width: 100vw;
    height: 100vh;
    background: rgba(0,0,0,0.75);
  }

  .modal {
    width: 90%;
    max-width: 600px;
    background-color: #eee;
    border-radius: 12px;
    position: fixed;
    z-index: 100000000;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    padding: 2rem;
    display: flex;
    flex-direction: column;

    .fa-times-circle {
      position: absolute;
      top: 8px;
      right: 8px;
      padding: 0;
      margin: 0;
      font-size: 2.2rem;
      color: $contrast2;
      cursor: pointer;
      text-shadow: 4px 3px 6px rgba(0, 0, 0, 0.2);
      transition: all 0.3s ease;

      &:hover {
        text-shadow: 0 0 8px rgba(255,255,255,0.75);
        transform: translate(4px, 3px);
      }
    }

    h4 {
      font-size: 2rem;
      width: auto;
      height: auto;
      text-align: center;
    }

    p {
      color: #666;
      font-weight: 300;
    }

    .img-box {
      width: 200px;
      height: 200px;
      border: 1px solid black;
      margin: 2rem auto;
      overflow: hidden;

      img {
        max-width: 100%;
      }
    }
  }
}

sources.style\\footer.scss:
footer {
  background-color: #222;
  color: #ccc;

  .link-wrapper {
    padding: 2rem;
    display: flex;
    flex-direction: column;
    align-items: center;

    .section-footer {
      margin: 2rem 0;
    }

    h3 {
      color: #fff;
      font-weight: 300;
      font-size: 1.5rem;

      span {
        color: $contrast2;
        font-size: 2rem;
        font-weight: 900;
      }
    }

    p {
      margin: 1rem 0;

      .fas {
        color: $contrast2;
      }

      a {
        color: #fff;
        text-decoration: none;
        transition: color 0.3s ease;

        &:hover {
          color: $contrast2;
        }
      }
    }

    ul {
      margin-left: 1rem;

      li {
        margin: 6px 0;
      }
    }

    #email {
      width: 250px;
      padding: 6px;
      border-radius: 4px;
      background-color: #444;
      color: #999;
      border: none;
      margin: 8px 0;
    }

    #message {
      width: 250px;
      padding: 6px;
      border-radius: 4px;
      background-color: #444;
      color: #999;
      border: none;
      margin: 8px 0;
    }

    #submit {
      padding: 6px 1rem;
      border-radius: 4px;
      background-color: #444;
      font-size: 1.2rem;
      color: $contrast2;
      border: none;
      cursor: pointer;
      transition: all 0.3s ease;

      &:hover {
        background-color: #555;
        color: lighten($contrast2, 10%);
      }
    }

    form {
      white-space: wrap;
    }
  }

  .copyright {
    background-color: #111;
    color: #555;
    text-align: center;
    padding: 4px 0;
  }
}

sources.style\\responsivity.scss:
@media (min-width: 600px) {
  .img-highlight-wrapper {
    max-width: 90%;
    height: 100%;
  }

  footer {
    .link-wrapper {
      flex-direction: row;
      align-items: flex-start;
      justify-content: space-around;
      flex-wrap: wrap;

      .section-footer {
        margin: 2rem;
        width: 30%;

        #email, #message {
          width: 100%;
        }
      }
    }
  }

  .img-carousel-container {
    height: 575px;
  }
}

@media (min-width: 768px){

  #hero {
    padding: 0 4rem;
    background: #111;
    display: flex;

    .img-hero {
      max-height: 100%;
      position: relative;
      left: 0;
      z-index: 1 !important;
      width: auto !important; 
      box-shadow: inset 50px 0 50px rgba(0, 0, 0, 1) !important;
    }

    .overlay {
      top: 0;
      left: 0;
      width: 300px !important;
      height: 200px !important;
      z-index: 10;
      background: linear-gradient(90deg, rgba(255, 255, 255, 0),
                                            rgba(255, 255, 255, 0),
                                            rgba(255, 255, 255, 0),
                                            rgba(17,17,17,1)) !important;
      // border: 1px solid white;
    }

    .logo-link {
      position: absolute;
      right: 20%;
      z-index: 10;


      .logo {
        max-height: 125px;
      }
    }
  }

  .mobile-menu-wrapper {
    display: none !important;
  }

  nav {
    display: flex !important;

  }

  .img-carousel-container {
    height: 650px !important;
  }

  footer {
    .link-wrapper {
      padding: 2rem 0;
      display: flex;
      flex-direction: row;
      align-items: flex-start;
      justify-content: space-around;

      .section-footer {
        margin: 0 2rem;
        width: 25%;

        #email, #message {
          width: 100%;
        }
      }
    }
  }

}

@media (min-width: 1024px){
  #hero {
    height: 350px !important;

    .overlay {
      width: 525px !important;
      height: 350px !important;
    }

    .logo-link {
      right: 15%;

      .logo {
        height: 180px;
        max-height: 180px !important;
      }
    }
  }
}

@media (min-width: 1200px){
  .logo-link {
    right: 20%;

    .logo {
      height: 200px;
      max-height: 200px !important;
    }
  }
}
